<script>
    import { spring } from "svelte/motion";

    const axis = spring(
        { width: 0, height: 0 },
        {
            stiffness: 0.1,
            damping: 0.8,
        }
    );

    const angle = spring(0, {
        stiffness: 0.1,
        damping: 0.8,
    });

    const zPos = spring(0, {
        stiffness: 0.1,
        damping: 0.3,
    });

    const height = 183;
    const width = 159.75;

    let mouseTop = 0;
    let mouseLeft = 0;

    let over = false;
    let debug = false;

    function mouseOver() {
        zPos.set(250);
        over = true;
    }
    function mouseLeave() {
        zPos.set(0);
        angle.set(0);
        axis.set({ width: 0, height: 0 });

        console.log($angle);
        console.log($axis);
        over = false;
    }
    function mouseMove(e) {
        if (over) {
            mouseTop = e.clientY;
            mouseLeft = e.clientX;

            let normalX = (e.layerX / width - 0.5) * 2;
            let normalY = (e.layerY / height - 0.5) * 2;

            let absAngleMultiplier = Math.sqrt(
                normalX * normalX + normalY * normalY
            );
            angle.set(absAngleMultiplier * 15);
            axis.set({ width: normalX, height: -1 * normalY });
        }
    }
</script>

